INSERT INTO `hongdou`(`id`, `book_name`, `author_name`, `tags`, `categories`, `intro`, `words_num`, `popularity`, `share_url`) VALUES (5, '娇妻临门，男神老公请接招', '公子悦', '豪门;都市', '女频;现代言情;总裁豪门', '霍夜寒，站在世界财富顶端的男人，皇族中最尊贵的太子爷，却被女人误以为是出来卖的牛郎！裴若若耷拉着小脑袋，表示很无辜。谁能来告诉她，花钱找牛郎，怎么一眨眼，就变成牛郎的小情人了？某男冷艳的鄙夷一眼看她；“我看上你了，赶快来谢恩！”裴若若暴躁，某天早上扶着自己腰酸背疼的腰，大闹：“我要解约！”', '196.8w+', '15w+', 'https://qb0.hongdouyuedu.com/cms_files/hdwwa1/mhdv6/reader/#?novel_code=35771561059481948&platform=wwa1&plattype=aphone&online=1&_from=h5');
